mapper.select:
  	select 
    
    from cu_customer
     where 1=1
      
        AND name like "%"#{cust.name,jdbcType=VARCHAR}"%"
      
      
        AND id_card_no = #{cust.idCardNo,jdbcType=VARCHAR}
      
      
        AND email = #{cust.email,jdbcType=VARCHAR}
      
     
        AND birthday = #{cust.birthday,jdbcType=DATE}
      
     
        AND mobile = #{cust.mobile,jdbcType=VARCHAR}
      
      
        AND tel = #{cust.tel,jdbcType=VARCHAR}
      
      
        AND nickname = #{cust.nickname,jdbcType=VARCHAR}
      
       AND deleted_flag=0 
        order by last_updated_time desc
       
	 	 limit #{page.offset},#{page.size}

	select 
  count(1)
  from cu_customer
   where 1=1
    
      AND name like "%"#{name,jdbcType=VARCHAR}"%"
    
    
      AND id_card_no = #{idCardNo,jdbcType=VARCHAR}
    
    
      AND email = #{email,jdbcType=VARCHAR}
    
   
      AND birthday = #{birthday,jdbcType=DATE}
    
   
      AND mobile = #{mobile,jdbcType=VARCHAR}
    
    
      AND tel = #{tel,jdbcType=VARCHAR}
    
    
      AND nickname = #{nickname,jdbcType=VARCHAR}
    
     AND deleted_flag=0

	select 
  
  from cu_customer
   where deleted_flag=0 and id= #{id,jdbcType=VARCHAR}
