mapper.select:

select 

from cu_org_customer
where customer_id = #{customerId,jdbcType=VARCHAR}
  and org_id = #{orgId,jdbcType=INTEGER}

mapper.delete:

delete from cu_org_customer
where customer_id = #{customerId,jdbcType=VARCHAR}
  and org_id = #{orgId,jdbcType=INTEGER}

mapper.update:

update cu_org_customer
set version_no = #{versionNo,jdbcType=INTEGER},
  created_by_id = #{createdById,jdbcType=BIGINT},
  created_time = #{createdTime,jdbcType=TIMESTAMP},
  last_updated_by_id = #{lastUpdatedById,jdbcType=BIGINT},
  last_updated_time = #{lastUpdatedTime,jdbcType=TIMESTAMP},
  deleted_flag = #{deletedFlag,jdbcType=BIT}
where customer_id = #{customerId,jdbcType=VARCHAR}
  and org_id = #{orgId,jdbcType=INTEGER}
